mapper.update:
update student
set name = #{name,jdbcType=VARCHAR},
  gender = #{gender,jdbcType=CHAR},
  age = #{age,jdbcType=INTEGER},
  birthdate = #{birthdate,jdbcType=DATE}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, name, gender, age, birthdate
from student
where id = #{id,jdbcType=INTEGER}
